import React, {useState} from "react";
import { useParams, useHistory } from "react-router-dom";
import { Button, Form, FormGroup, Label, Input} from "reactstrap";

function MenuForm() {
  const { item } = useParams();
  const [newItem, setNewItem] = useState({});
  const history = useHistory();
  // creates history object that stores the current location
  // also stores forward and backward methods

  function handleChange(e) {
    setNewItem(e.target.value);
  }

  useEffect(() => {
    async function storeItem(obj) {
      try {
        await axios.post(`http://localhost:5000/${item}s`, {
          // passing in q as a param for the query searchTerm
          params: { obj}
        });
      }
      catch (e) {
        console.log(e)
      }
    }
    storeItem(newItem);
    // this will run on initial render and whenever name changes
  }, [newItem]);
 

  function handleSubmit(evt) {
    evt.preventDefault();
    newItem= {
        "id": {itemName},
        "name": {itemName},
        "description": {description},
        "recipe": {recipe},
        "serve": {serve}
      }
    setNewItem(newItem);
    // redirect with history.push and the path
    // after the email form is submitted it redirects to the root
    // can also go back and forward unlike redirect
    history.push('/')
  }


  return (
    <section>
        <h2>Add a new {item}!</h2>
      <Form>
        <FormGroup>
          <Label for="name">Item Name</Label>
          <Input type="text" name="name" id="name" value={itemName} />
        </FormGroup>
        <FormGroup>
          <Label for="description">Short description</Label>
          <Input type="text" name="description" id="description" placeholder="An ice-cold, refreshing classic." value={description}/>
        </FormGroup>
        <FormGroup>
          <Label for="recipe">Recipe</Label>
          <Input type="textarea" name="recipe" id="recipe" placeholder="Mix 3 parts vodka & 1 part dry vermouth." value={recipe}/>
        </FormGroup>
        <FormGroup>
          <Label for="serve">How should this be served?</Label>
          <Input type="textarea" name="serve" id="serve" placeholder="Serve very cold, straight up." value={serve}/>
        </FormGroup>
        <Button>Submit</Button>
      </Form>
    </section>
  );
}

export default MenuForm;
